import React, { Fragment, useState } from 'react';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';
import ReactTable from "./ReactTable"

const ReactForm = (props) => {
    // const [name, setName] = useState('')
    // const [email, setEmail] = useState('')
    // const [password, setPassword] = useState('')
    // const [text, setText] = useState('')
    const [state, setState] = useState({
        name: "",
        email: "",
        password: "",
        text: "",
    })

    const [values, setValues] = useState([])

    // Form submit

    const onFormSubmit = (e) => {
        e.preventDefault()
        setValues([...values, ...state])
        console.log(values, "state")
    }

    return (
        <Fragment>
            <h1 className="text-center">React Strap Form</h1>
            <Form onSubmit={onFormSubmit}>
                <FormGroup>
                    <Label for="name">Name</Label>
                    <Input value={state.name} type="name" name="name" onChange={(e) => setState(e.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Label for="exampleEmail">Email</Label>
                    <Input value={state.email} type="email" name="email" id="exampleEmail" placeholder="with a placeholder" onChange={(e) => setState(e.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Label for="examplePassword">Password</Label>
                    <Input value={state.password} type="password" name="password" id="examplePassword" placeholder="password placeholder" onChange={(e) => setState(e.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Label for="exampleText">Text Area</Label>
                    <Input value={state.text} type="textarea" name="text" id="exampleText" onChange={(e) => setState(e.target.value)} />
                </FormGroup>
                <Button>Submit</Button>
            </Form>
            <ReactTable />
        </Fragment>
    );
}

export default ReactForm;
