import React, { Fragment, useState } from 'react';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';
import ReactTable from "./ReactTable"

const ReactForm = (props) => {
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [text, setText] = useState('')
    const [values, setValues] = useState([])

    // Form submit

    const onFormSubmit = (e) => {
        e.preventDefault()
        setValues([...values, { name }, { email }, { password }, { text }])
    }

    return (
        <Fragment>
            <h1 className="text-center">React Strap Form</h1>
            <Form onSubmit={onFormSubmit}>
                <FormGroup>
                    <Label for="name">Name</Label>
                    <Input value={name} type="name" name="name" onChange={(e) => setName(e.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Label for="exampleEmail">Email</Label>
                    <Input value={email} type="email" name="email" id="exampleEmail" placeholder="with a placeholder" onChange={(e) => setEmail(e.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Label for="examplePassword">Password</Label>
                    <Input value={password} type="password" name="password" id="examplePassword" placeholder="password placeholder" onChange={(e) => setPassword(e.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Label for="exampleText">Text Area</Label>
                    <Input value={text} type="textarea" name="text" id="exampleText" onChange={(e) => setText(e.target.value)} />
                </FormGroup>
                <Button>Submit</Button>
            </Form>
            <ReactTable values={values} />
        </Fragment>
    );
}

export default ReactForm;
