import React, { Fragment, useState } from 'react';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';
import ReactTable from "./ReactTable"

const ReactForm = (props) => {
    const [state, setState] = useState({
        name: "",
        email: "",
        password: "",
        text: "",
    })

    const [values, setValues] = useState([])

    const onHandleChange = (e) => {
        const target = e.target
        setState({
            ...state,
            [target.name]: target.value
        })
    }

    const onFormSubmit = (e) => {
        e.preventDefault()
        setValues([...values, state])
    }

    return (
        <Fragment>
            <h1 className="text-center">React Strap Form</h1>
            <Form onSubmit={onFormSubmit} >
                <FormGroup>
                    <Label for="name">Name</Label>
                    <Input value={state.name} type="name" name="name" onChange={onHandleChange} />
                </FormGroup>
                <FormGroup>
                    <Label for="exampleEmail">Email</Label>
                    <Input value={state.email} type="email" name="email" id="exampleEmail" placeholder="with a placeholder" onChange={onHandleChange} />
                </FormGroup>
                <FormGroup>
                    <Label for="examplePassword">Password</Label>
                    <Input value={state.password} type="password" name="password" id="examplePassword" placeholder="password placeholder" onChange={onHandleChange} />
                </FormGroup>
                <FormGroup>
                    <Label for="exampleText">Text Area</Label>
                    <Input value={state.text} type="textarea" name="text" id="exampleText" onChange={onHandleChange} />
                </FormGroup>
                <Button>Submit</Button>
            </Form>
            <ReactTable values={values} />
        </Fragment>
    );
}

export default ReactForm;
